import { Button, TextField, Typography } from '@mui/material';
import { Box } from '@mui/system';
import React from 'react';
import { useState } from 'react';
import axios from 'axios';
import { useDispatch } from "react-redux";
import { authActions } from "../store";
import { useNavigate } from "react-router-dom";

const Auth = () => {
    const naviagte = useNavigate();
    const dispath = useDispatch();
    const [inputs, setInputs] = useState({
        name: "",
        email: "",
        password: "",
    });
    const [errors, setErrors] = useState({});

    const createNewPet = (e) => {
        e.preventDefault();
        const pets = { name, type, description, skillOne, skillTwo };
        axios.post("http://localhost:8000/api/pets/new", pets)
            .then(res => {
                if (res.data.error) {
                    setErrors(res.data.error.errors);
                } else {
                    navigate("/");
                }
            })
            .catch(err => console.log(err));
    }
    const [isSignup, setIsSignup] = useState(false);
    const handleChange = (e) => {
        setInputs((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value,
        }));
    };
    const sendRequest = async (type = "login") => {
        const res = await axios
            .post(`http://localhost:5000/api/user/${type}`, {
                name: inputs.name,
                email: inputs.email,
                password: inputs.password,
            })
            .catch((err) => console.log(err));

        const data = await res.data;
        console.log(data);
        return data;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(inputs);
        if (isSignup) {
            sendRequest("signup")
                .then((data) => localStorage.setItem("userId", data.user._id))
                .then(() => dispath(authActions.login()))
                .then(() => naviagte("/blogs"));
        } else {
            sendRequest()
                .then((data) => localStorage.setItem("userId", data.user._id))
                .then(() => dispath(authActions.login()))
                .then(() => naviagte("/blogs"));
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <Box
                    maxWidth={400}
                    display="flex"
                    flexDirection={"column"}
                    alignItems="center"
                    justifyContent={"center"}
                    boxShadow="10px 10px 20px #ccc"
                    padding={3}
                    margin="auto"
                    marginTop={5}
                    borderRadius={5}
                >
                    <Typography variant="h2" padding={3} textAlign="center">
                        {isSignup ? "Signup" : "Login"}
                    </Typography>
                    {isSignup && (
                        <TextField
                            name="name"
                            onChange={handleChange}
                            value={inputs.name}
                            placeholder="Name"
                            margin="normal"
                        />
                    )}{" "}
                    {errors.name ? <p className="text-danger ">{errors.name.properties.message}</p> : ""}
                    <TextField
                        name="email"
                        onChange={handleChange}
                        value={inputs.email}
                        type={"email"}
                        placeholder="Email"
                        margin="normal"
                    />
                    <TextField
                        name="password"
                        onChange={handleChange}
                        value={inputs.password}
                        type={"password"}
                        placeholder="Password"
                        margin="normal"
                    />
                    <Button
                        type="submit"
                        variant="contained"
                        sx={{ borderRadius: 3, marginTop: 3, background:
                            "linear-gradient(90deg, rgba(76,161,175,1) 0%, rgba(196,224,229,1) 0%)" }}
                    >
                        Submit
                    </Button>
                    <Button
                        onClick={() => setIsSignup(!isSignup)}
                        sx={{ borderRadius: 3, marginTop: 3 }}
                    >
                        Change To {isSignup ? "Login" : "Signup"}
                    </Button>
                </Box>
            </form>
        </div>
    );
};
export default Auth;
