import React, { useState } from 'react';
import { AppBar, Button, Toolbar, Typography, Box, Tabs, Tab } from '@mui/material'
import { Link } from 'react-router-dom';
import { useSelector } from "react-redux";

const Header = () => {
  const dispath = useDispatch();
  const isLoggedIn = useSelector((state) => state.isLoggedIn);
  const [value, setValue] = useState();
  return (
    <AppBar position='sticky' sx={{ background: "linear-gradient(90deg, rgba(131,58,180,1) 0%, rgba(253,29,29,1) 50%, rgba(252,176,69,1) 100%)"}}>
      <Toolbar>
        <Typography variant='h4'>BlogsApp</Typography>
        { isLoggedIn && <Box display="flex" marginLeft={"auto"} marginRight={"auto"}> 
          <Tabs textColor='inherit' value={value} onChange={(e, val) => setValue(val)}>
            <Tab LinkComponent={Link} to="/blogs" label="All Blogs" />
            <Tab LinkComponent={Link} to="/myBlogs" label="My Blogs" />
            {/* <Tab label="Add Blogs" /> */}
          </Tabs>
        </Box>}
        <Box display="flex" marginLeft="auto">
          { !isLoggedIn && <> <Button LinkComponent={Link} to="/auth" variant='contained' sx={{ margin: 1, borderRadius: 10 }} color='primary'>Login</Button>
          <Button LinkComponent={Link} to="/auth" variant='contained' sx={{ margin: 1, borderRadius: 10 }} color='primary'>Signup</Button></>}
          { isLoggedIn && <Button onClick={() => dispath(authActions.logout())} LinkComponent={Link} to="/auth" variant='contained' sx={{ margin: 1, borderRadius: 10 }} color='primary'>Logout</Button>}
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
